(str, substring, endChar, removeString) => {
  const startIndex = str.indexOf(substring);
  if (startIndex === -1) {
    return '';
  }
  const endIndex = str.indexOf(endChar, startIndex + substring.length);
  if (endIndex === -1) {
    return '';
  }
  let info = str.substring(startIndex + substring.length, endIndex);
  if (removeString) {
    info = info.replace(removeString, '');
  }
  console.log(info.trim());
  return info.trim();
}

